-- Migration 009: Genre Tropes System
-- Adds tropes library with genre associations for story concept generation

CREATE TABLE IF NOT EXISTS genre_tropes (
    id TEXT PRIMARY KEY,
    trope_name TEXT NOT NULL,
    description TEXT NOT NULL,
    genre TEXT NOT NULL,                    -- Primary genre this trope belongs to
    subgenre TEXT,                          -- Optional specific subgenre
    trope_type TEXT NOT NULL CHECK(trope_type IN ('character', 'plot', 'setting', 'relationship', 'theme', 'device')),
    usage_frequency TEXT NOT NULL CHECK(usage_frequency IN ('common', 'moderate', 'rare')) DEFAULT 'moderate',
    compatibility_tags TEXT,                -- JSON array of genres/subgenres this works well with
    warning_tags TEXT,                      -- JSON array of common criticisms or overuse warnings
    examples TEXT,                          -- JSON array of famous examples using this trope
    subversions TEXT,                       -- JSON array of ways to subvert/twist this trope
    created_at TEXT DEFAULT (datetime('now')),
    updated_at TEXT DEFAULT (datetime('now'))
);

CREATE INDEX IF NOT EXISTS idx_genre_tropes_genre ON genre_tropes(genre);
CREATE INDEX IF NOT EXISTS idx_genre_tropes_subgenre ON genre_tropes(subgenre);
CREATE INDEX IF NOT EXISTS idx_genre_tropes_type ON genre_tropes(trope_type);
CREATE INDEX IF NOT EXISTS idx_genre_tropes_frequency ON genre_tropes(usage_frequency);

-- Table to track user-selected tropes for concepts
CREATE TABLE IF NOT EXISTS concept_tropes (
    id TEXT PRIMARY KEY,
    concept_id TEXT NOT NULL,               -- References saved_concepts.id
    trope_id TEXT NOT NULL,                 -- References genre_tropes.id
    preference TEXT NOT NULL CHECK(preference IN ('include', 'exclude', 'subvert')) DEFAULT 'include',
    notes TEXT,                             -- User notes on how to use this trope
    created_at TEXT DEFAULT (datetime('now')),
    FOREIGN KEY (concept_id) REFERENCES saved_concepts(id) ON DELETE CASCADE,
    FOREIGN KEY (trope_id) REFERENCES genre_tropes(id) ON DELETE CASCADE
);

CREATE INDEX IF NOT EXISTS idx_concept_tropes_concept ON concept_tropes(concept_id);
CREATE INDEX IF NOT EXISTS idx_concept_tropes_trope ON concept_tropes(trope_id);
CREATE INDEX IF NOT EXISTS idx_concept_tropes_preference ON concept_tropes(preference);

-- Seed common tropes for Fantasy genre
INSERT OR IGNORE INTO genre_tropes (id, trope_name, description, genre, trope_type, usage_frequency, compatibility_tags, examples, subversions) VALUES
('trope_fantasy_chosen_one', 'The Chosen One', 'A character destined by prophecy or fate to save the world or accomplish a great deed.', 'fantasy', 'character', 'common', '["epic-fantasy", "high-fantasy", "young-adult"]', '["Harry Potter", "The Matrix", "Star Wars"]', '["The chosen one refuses the call", "Multiple chosen ones exist", "The prophecy is misinterpreted", "The chosen one fails"]'),
('trope_fantasy_magic_system', 'Hard Magic System', 'A magic system with clear rules, limitations, and costs that readers can understand and predict.', 'fantasy', 'device', 'moderate', '["epic-fantasy", "high-fantasy", "urban-fantasy"]', '["Brandon Sanderson works", "The Kingkiller Chronicle", "Avatar: The Last Airbender"]', '["Magic follows no rules", "The cost of magic changes randomly", "Only the protagonist can break the rules"]'),
('trope_fantasy_ancient_evil', 'Ancient Evil Awakens', 'A powerful malevolent force that has been dormant for ages suddenly returns to threaten the world.', 'fantasy', 'plot', 'common', '["epic-fantasy", "dark-fantasy", "horror"]', '["The Lord of the Rings", "The Wheel of Time", "The Witcher"]', '["The ancient evil is actually good", "The evil never fully awakens", "The awakening was intentional by the heroes"]'),
('trope_fantasy_found_family', 'Found Family', 'A group of unrelated characters who form deep familial bonds through shared experiences and loyalty.', 'fantasy', 'relationship', 'common', '["epic-fantasy", "urban-fantasy", "cozy-fantasy", "adventure"]', '["The Guardians of the Galaxy", "One Piece", "The Chronicles of Narnia"]', '["The found family betrays each other", "Found family becomes toxic", "Biological family proves stronger"]'),
('trope_fantasy_mentor_dies', 'Mentor''s Death', 'The wise teacher or guide is killed, forcing the protagonist to continue their journey alone.', 'fantasy', 'plot', 'common', '["epic-fantasy", "coming-of-age", "adventure"]', '["Star Wars (Obi-Wan)", "Harry Potter (Dumbledore)", "The Name of the Wind"]', '["The mentor faked their death", "The mentor becomes the antagonist", "The mentor dies but returns as a ghost"]'),
('trope_fantasy_hidden_heritage', 'Secret Royal/Noble Blood', 'The protagonist discovers they are actually descended from royalty, nobility, or a powerful bloodline.', 'fantasy', 'character', 'common', '["epic-fantasy", "high-fantasy", "romantasy"]', '["Star Wars", "The Sword of Shannara", "Throne of Glass"]', '["The heritage is fake/planted", "The heritage makes things worse", "Everyone has this heritage"]'),
('trope_fantasy_prophecy', 'Cryptic Prophecy', 'An ancient prediction that drives the plot but is open to interpretation until fulfilled.', 'fantasy', 'plot', 'moderate', '["epic-fantasy", "high-fantasy", "dark-fantasy"]', '["The Matrix", "Dune", "The Wheel of Time"]', '["The prophecy was a lie", "Multiple conflicting prophecies", "The prophecy is self-fulfilling"]'),
('trope_fantasy_quest_macguffin', 'Quest for the MacGuffin', 'The heroes must find a powerful artifact, object, or person that will solve their problems.', 'fantasy', 'plot', 'common', '["epic-fantasy", "adventure", "high-fantasy"]', '["The Lord of the Rings", "Indiana Jones", "The Dark Tower"]', '["The MacGuffin is worthless", "The journey matters more than the item", "The MacGuffin is destroyed early"]'),
('trope_fantasy_dark_lord', 'The Dark Lord', 'An all-powerful evil overlord who seeks to conquer or destroy the world, often with an army.', 'fantasy', 'character', 'moderate', '["epic-fantasy", "high-fantasy", "grimdark"]', '["Sauron (LOTR)", "Voldemort", "Darth Vader"]', '["The Dark Lord is the hero", "The Dark Lord is actually weak", "There is no single Dark Lord"]'),
('trope_fantasy_magic_school', 'Academy of Magic', 'Characters attend an institution dedicated to teaching magic, often with house systems or rivalries.', 'fantasy', 'setting', 'moderate', '["urban-fantasy", "young-adult", "high-fantasy"]', '["Harry Potter", "The Magicians", "A Deadly Education"]', '["The school is a trap", "Magic can''t be taught", "The school is the villain"]'),
('trope_fantasy_portal_world', 'Portal to Another World', 'Characters travel from the mundane world to a magical realm through a portal or gateway.', 'fantasy', 'plot', 'moderate', '["urban-fantasy", "young-adult", "portal-fantasy"]', '["The Chronicles of Narnia", "Alice in Wonderland", "The Magicians"]', '["The portal is one-way", "Both worlds merge", "The other world invades ours"]'),
('trope_fantasy_sentient_weapon', 'Legendary Sentient Weapon', 'A powerful weapon with its own consciousness, personality, or will that aids (or manipulates) the wielder.', 'fantasy', 'device', 'moderate', '["epic-fantasy", "sword-and-sorcery", "dark-fantasy"]', '["Stormbringer (Elric)", "Nightblood (Warbreaker)", "Excalibur"]', '["The weapon is evil", "The weapon lies constantly", "The weapon is actually weak"]'),
('trope_fantasy_dragons', 'Dragons as Power', 'Dragons as intelligent, powerful beings that can be allies, enemies, or forces of nature.', 'fantasy', 'character', 'common', '["epic-fantasy", "high-fantasy", "romantasy"]', '["The Hobbit", "Eragon", "Game of Thrones"]', '["Dragons are weak/endangered", "Dragons are commonplace", "Dragons are actually parasites"]'),
('trope_fantasy_farmboy_hero', 'Farm Boy to Hero', 'An ordinary young person from a rural background rises to become a legendary hero.', 'fantasy', 'character', 'common', '["epic-fantasy", "coming-of-age", "adventure"]', '["Luke Skywalker", "Rand al''Thor", "Garion (The Belgariad)"]', '["The farm boy fails", "The farm boy was never ordinary", "The farm boy returns to farming"]'),
('trope_fantasy_immortality_curse', 'Immortality as a Curse', 'A character who cannot die faces existential anguish, loss of loved ones, or corruption over time.', 'fantasy', 'theme', 'moderate', '["dark-fantasy", "philosophical", "grimdark"]', '["Highlander", "Interview with the Vampire", "The Old Guard"]', '["Immortality is actually great", "Immortality can be ended easily", "Everyone becomes immortal"]');

