('trope_sf_post_scarcity', 'Post-Scarcity Society', 'Technology has eliminated poverty and want, changing the nature of human society and conflict.', 'science-fiction', 'setting', 'moderate', '["utopian", "space-opera", "philosophical"]', '["Star Trek", "The Culture", "The Expanse (partial)"]', '["Post-scarcity fails", "New scarcity emerges", "People reject abundance"]'),
('trope_sf_cyborg_identity', 'More Machine Than Human', 'A character with extensive cybernetic enhancements questions what remains of their humanity.', 'science-fiction', 'theme', 'moderate', '["cyberpunk", "transhumanism", "noir"]', '["Ghost in the Shell", "Robocop", "Deus Ex"]', '["Cyborg is more human", "Biology is obsolete", "Everyone is cyborged"]'),
('trope_sf_alien_artifact', 'Ancient Alien Technology', 'Discovery of powerful technology left behind by an extinct or absent alien civilization.', 'science-fiction', 'plot', 'moderate', '["space-opera", "hard-sf", "mystery"]', '["Mass Effect", "Ringworld", "2001: A Space Odyssey"]', '["The artifact is worthless", "Artifact is a trap", "Aliens are still around"]'),
